import axios from 'axios';
import React, {useState} from 'react'
import { useHistory } from 'react-router';

const initialFormValue = {
    username: '',
    password: ''
};
const initialError = {
    username: '',
    password: ''
};

const LoginForm = () => {
    const [formValue, setFormValue] = useState(initialFormValue);
    const [error, setError] = useState(initialError);
    const { push } = useHistory();


    const change = (evt) =>{
        const { name, value } = evt.target
        setFormValue({ ...formValue, [name]:value});
    };

    const submit = (event) =>  {
        event.preventDefault();
        const loginInfo = {
            username: formValue.username.trim(),
            password: formValue.password.trim()
        }
    //     axios.post(axios.post('https://ft-african-marketplace-05-back.herokuapp.com/api/auth/login')
    //     .then(res=>{
    //         console.log(res)
    //     })
    //     .catch(err=>{console.log(err)});
    }

    return (

        <form onSubmit = {submit}>
            <label>Login:</label>
             <br/>
            <input type='text' name='username' value={formValue.username} placeholder='Username' onChange={change}/>
            <br/>
            <input type='password' name='password' value={formValue.password} placeholder='Password' onChange={change}/>
            <br/>
            <button className='submit'>Submit</button>
        </form>

    )
}

export default LoginForm
