import React from 'react';
import { Link } from 'react-router-dom';
import styled from "styled-components";

const StyledHeaderLoggedOut = styled.header`
display:flex;
justify-content: space-between;
align-items:center;

h2 {
    padding-left: 3%;
}

nav {
    display:flex;
    width: 25%;
    justify-content: space-between;
    margin-right: 3%;
}
a {
    text-decoration: none;
  }

`
const StyledHeaderLoggedIn = styled.header`
display:flex;
justify-content: space-between;
align-items:center;

h2 {
    padding-left: 3%;
}

nav {
    display:flex;
    width: 40%;
    border: 1px black solid;
    margin-right: 3%;
}
a {
    text-decoration: none;
  }

`


const Header=(props)=>{
    const { loggedIn } = props;

    return (
        <>
        {loggedIn && 
        <StyledHeaderLoggedOut>
            <h2>African Marketplace</h2>
            <nav>
                <Link to='/'>Home</Link>
                <Link to='/login'>Login</Link>
                <Link to='/signup'>Sign up</Link>    
            </nav>
        </StyledHeaderLoggedOut>}
        {!loggedIn &&
        <StyledHeaderLoggedIn>
            <h2>African Marketplace</h2>
            <nav>
                <Link to='/'>Home</Link>
                <Link to='/itemlist'>Marketplace</Link>
                <Link to='/itemform'>Add Item</Link>
                <Link to='/logout'>Logout</Link>
            </nav>
        </StyledHeaderLoggedIn>}
        </>


    );
};
export default Header;
