import React, { useState } from 'react'
import Item from './Item';

const initialSearchTerm = '';
const initialSearchResult = {};

export default function Search(props) {
  const { items } = props;
  const [searchTerm, setSearchTerm] = useState(initialSearchTerm);
  const [searchResult, setSearchResult] = useState(initialSearchResult);

  const getSearchItem = (object) => {
    const searchItem = items.filter(elem => {
      return elem.name.toLowerCase().includes(object);
    });
    return searchItem;
  }
  const changeHandler = event => {
    console.log(event.target.value);
    setSearchTerm(event.target.value);
  }
  const onSubmit= evt=>{
    evt.preventDefault();
    items.filter
  };

  return (

    <>
    <form onSubmit={onSubmit}>
      <input type='text' value={searchTerm} onChange={changeHandler}/>
      <button>Search</button>
    </form>
    <div>
      <Item key={searchResult.id} item={searchResult}/>
    </div>
    </>

  )
}
